    <div>
    {question.map((q,index)=>
      <ul>
        <li key={index}>
          {q.question}
          <div>{q.answer.map((ans,i)=><button onClick={(i)=>{correctanswer(i)}}>{ans}</button>)}</div>
        </li>
      </ul>
    )}
    </div>
  );
}

export default App;
